	testSteps.eachWithIndex{testStep, i ->
		if (testStep.config.type == 'restrequest') {
			requestTestStep = testStep
		}
	}

	return requestTestStep
}

import groovy.json.JsonSlurper

def jslurper = new JsonSlurper()
def username = "New_SoapUI-${new Date().getTime()}"
def newUserProfile = """
	{
		"lang": "ru",
		"username": "${username}@test.com"
	}
"""
def testRun
def user_id = testRunner.testCase.testSuite.getPropertyValue("user_id")

// set request body
getRequestTestStep().setPropertyValue("Request", newUserProfile)
newUserProfile = jslurper.parseText(newUserProfile)
// run test step
testRun = getRequestTestStep().run(testRunner, context)

// validate recived JSON contains username
response = jslurper.parseText(testRun.response.responseContent)
assert response._id == user_id
assert response.username == newUserProfile.username
assert response.lang == newUserProfile.lang
assert response.activeProject == null //since we didn't set active project yet it is null
assert response.projects == []
assert !response.salt
assert !response.hashedPassword

testRunner.testCase.testSuite.setPropertyValue("username", username)

/// PRIVATE FUNCTIONS
def getRequestTestStep() {
	def requestTestStep = null
	def testSteps = context.testCase.getTestStepList()

	testSteps.eachWithIndex{testStep, i ->
		if (testStep.config.type == 'restrequest') {
			requestTestStep = testStep
		}
	}

	return requestTestStep
}

