def accountDestination = accountSrouce
def category = testRunner.testCase.testSuite.getPropertyValue("project_category")
def updatedTransaction
def transactionToUpdate = """{
		"note": "Updated Transaction - ${new Date().getTime()}",
		"value" : 1667,
		"type": "expense",
		"accountSource": "${accountSrouce}",
		"accountDestination": "${accountDestination}",
		"category": "${category}"
}"""
def testRun

// set request body
getRequestTestStep().setPropertyValue("Request", transactionToUpdate)

//convert string to JSON
transactionToUpdate = jslurper.parseText(transactionToUpdate)

// run test step
testRun = getRequestTestStep().run(testRunner, context)

updatedTransaction = jslurper.parseText(testRun.response.responseContent)

// validate recived response
assert  updatedTransaction.note == transactionToUpdate.note
assert  updatedTransaction.type == transactionToUpdate.type
assert  updatedTransaction.value == transactionToUpdate.value
assert  updatedTransaction.category == transactionToUpdate.category
assert  updatedTransaction.accountSource == transactionToUpdate.accountSource
assert  updatedTransaction.accountDestination == transactionToUpdate.accountDestination

// set test suite properties
testRunner.testCase.testSuite.setPropertyValue("transaction_id", updatedTransaction._id)

